/*E5拨号后默认空闲时间*/
#define AT_E5_IDLE_TIME                         (600)

#define AT_GNA_E5_MAC_ADDR_LEN                  (32)                            /* MAC地址长度 */
#define AT_GNA_E5_MAC_ADDR_COLON_NUM            (5)                             /* MAC地址中冒号的个数*/
#define AT_GNA_E5_MAC_ADDR_LEN_BETWEEN_COLONS   (2)                             /* MAC地址中冒号之间的字符个数*/


/*****************************************************************************
  3 枚举定义
*****************************************************************************/
/*****************************************************************************
 结构名    : AT_E5_DIAL_MODE_ENUM_UINT32
 协议表格  :
 ASN.1描述 :
 结构说明  :拨号模式
*****************************************************************************/
enum AT_E5_DIAL_MODE_ENUM
{
    AT_E5_AUTO_DIAL_MODE,                                                       /*Auto dial mode, E5 is not support
                                                                                now*/
    AT_E5_MANUAL_DIAL_MODE,                                                     /*Manual dial mode*/
    AT_E5_DEMAND_DIAL_MODE,                                                     /*Demand dial mode*/
    AT_E5_DEMAND_DIAL_BUTT

};
typedef VOS_UINT32 AT_E5_DIAL_MODE_ENUM_UINT32;

/*****************************************************************************
 结构名    : AT_E5_DIAL_ENUM_UINT32
 协议表格  :
 ASN.1描述 :
 结构说明  :拨号方式
*****************************************************************************/
enum AT_E5_DIAL_ENUM
{
    AT_E5_DIAL_DOWN      = 0,                                                   /*断开拨号操作*/
    AT_E5_DIAL_UP        = 1,                                                   /*拨号操作*/
    AT_E5_DIAL_BUTT

};
typedef VOS_UINT32 AT_E5_DIAL_ENUM_UINT32;

