/*****************************************************************************
  7 STRUCT定义
*****************************************************************************/
/*****************************************************************************
 结构名    : AT_APP_DIAL_MODE_STRU
 协议表格  :
 ASN.1描述 :
 结构说明  :拨号方式
*****************************************************************************/
typedef struct
{
     /*
    拨号模式，0:  Auto dial mode, E5 is not support now，
    1:  Manual dial  mode，2:  Demand dial mode
    */
    VOS_UINT32                ulDialMode;
    /*
    This value is available only when dial mode is
    demand mode，单位为:秒
    */
    VOS_UINT32                ulIdleTime;
}AT_APP_DIAL_MODE_STRU;

/*****************************************************************************
 结构名    : AT_GNA_E5_GW_MAC_ADDR_STRU
 协议表格  :
 ASN.1描述 :
 结构说明  : 记录网关MAC地址NV项的结构
*****************************************************************************/
typedef struct
{
    VOS_UINT8                 ucStatus;                                         /* 1: NV有效标志位，0：无效 */
    VOS_UINT8                 ucReserved1;
    VOS_UINT8                 aucE5GwMacAddr[AT_GNA_E5_MAC_ADDR_LEN + 1];       /* MAC地址*/
} AT_GNA_E5_GW_MAC_ADDR_STRU;

